import React from "react";
import {Link} from "react-router-dom";
import '../components/Navbar.css';

class Navbar extends React.Component {
    state ={
        open: false
    } 

    handelMenu = (e) =>{
        this.setState(oldstate =>{
            const newState= {...oldstate.open};
            if(oldState.open === false) newState === true;
            else newState ===false;
            return {open:newState};   
        })     
    }


    render() {
    return(<nav>
    <a id="link" href="#" onClick={(e)=> this.handelMenu(e)} className={this.state.open ? "a_open" : "a_closed"}><span className={this.state.open ? "burger_open" : "burger_closed"} id="burger"></span></a>
        <ul  className={this.state.open ? "ul_open" : "ul_closed"}>
            <li><Link to='/'>Home</Link></li>
            <li><Link to='/about'>A propos</Link></li>
            <li><Link to='/events'>Stages</Link></li>
            <li><Link to='/cours'>Cours de Danse</Link></li>
            <li><Link to='/solidaire'>Activit√©s Solidaires</Link></li>
            <li><Link to='/contact'>Contact</Link></li>
        </ul>
    </nav>

    )}}

export default Navbar;
