import React, {useEffect, useState} from "react";

    const Events = () => {
        const[events, setNotes] = useState([{  //constante notes, fonstion setNotes
            date:'',
            content:'',
            place: ''
        }]) 

        useEffect(()=>{
            fetch("/events").then(res=>{
                if(res.ok){
                    return res.json()
                }
            }).then(jsonRes => setNotes(jsonRes));
        })
        return <div>
            <h1>Évènements</h1>
            {events.map(event =>
            <div>
                <p key={event}>{event.date}</p>
                <p key={event}>{event.content}</p>
                <p key={event}>{event.place}</p>
            </div>
            )}
        </div>
        
        }

export default Events;
