import {Link} from "react-router-dom";
import '../components/Navbar.css';

class Navbar extends React.Component {
    state ={
        open: false
    } 

    handelMenu = () =>{
        this.setState({open: true});
    }


    render() {
    return(<nav>
    <a id="link" href="#" onClick={handelMenu} className={this.state.open}><span className={this.state.open} id="burger"></span></a>
        <ul  className={this.state.open}>
            <li><Link to='/'>Home</Link></li>
            <li><Link to='/about'>A propos</Link></li>
            <li><Link to='/events'>Stages</Link></li>
            <li><Link to='/cours'>Cours de Danse</Link></li>
            <li><Link to='/solidaire'>Activit√©s Solidaires</Link></li>
            <li><Link to='/contact'>Contact</Link></li>
        </ul>
    </nav>

    )}}

export default Navbar;
