import React, {useEffect, useState} from "react";
import "./Events.css";

    const Events = () => {
        const[events, setNotes] = useState([{  //constante notes, fonstion setNotes
            date:'',
            content:'',
            place: ''
        }]) 

        useEffect(()=>{
            fetch("/events").then(res=>{
                if(res.ok){
                    return res.json()
                }
            }).then(jsonRes => setNotes(jsonRes));
        })
        return <div className="body_events">
            <h1>NOS STAGES</h1>
            <p>Vous trouverez dans cette section tous nos stages Ã  venir </p>
            {events.map(event =>
            <div>
                <p key={"date" + event._id}>{event.date}</p>
                <p key={"content" + event._id}>{event.content}</p>
                <p key={"place" + event._id}>{event.place}</p>
            </div>
            )}
        </div>
        
        }

export default Events;
